import React from 'react';
import {gsap} from 'gsap';

import { ScrollTrigger } from 'gsap/ScrollTrigger';
import './HomeSection.css';
import HomeImage from '../../Assets/Home Page Images/pexels-photo-547115.png';
import ZoomImage from '../../Assets/Home Page Images/ZoomImage.png';

gsap.registerPlugin(ScrollTrigger);
const HomeSection = () => {
 

    gsap.to ("#ZoomImage", {
      transform: "scale(2)",
      scrollTrigger: {
        trigger: "#HomeSection",
        scroller: "body",
        start: "top top",
        end: "bottom top",
        scrub: true,
        pin: "#HomeSection",
        markers:true,
        // onLeave: ()=> scroll.update,
      },
    })

  return (
    <div className='HomeSection' id='HomeSection' >
      <img src={ZoomImage} id='ZoomImage'/>
    <section id='section1' className='HomeSection' data-scroll>
        <img src={HomeImage} id='HomeImage' alt='HomeSection' data-scroll data-scroll-speed="-3"/>
    </section>
    </div>
  )
}

export default HomeSection;
