import React, { useRef, useEffect } from 'react';
import './HomeSection.css';
import Navbar from '../Common/Navbar';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import ZoomImage from '../../Assets/Home Page Images/ZoomImage.png';
import video from '../../Assets/Home Page Images/Video.mp4';

const HomeSection = () => {
  const ZoomImg = useRef(null);
  const section1 = useRef(null);
  
  gsap.registerPlugin(ScrollTrigger);

  useEffect(() => {
    // Create a GSAP timeline
    const tl = gsap.timeline({
      scrollTrigger: {
        trigger: ZoomImg.current,
        scroller: "body",
        start: "top top",
        end: "center top",
        pin: "#HomeSection",
        scrub: true,
        markers: true,
        onLeave: () => {
          // Chain the video animation
          tl.to(section1.current, {
            height: "90%",
            width: "90%",
            borderRadius: "20px",
            duration: 1, // Adjust duration as needed
          }, "<"); // Start the animation at the same time as the previous animation ends
        }
      }
    });

    // Animate the image scaling
    tl.to(ZoomImg.current, {
      scale: 8,
    });

  }, []);

  return (
    <div className='HomeSection' id='HomeSection'>
      <img src={ZoomImage} ref={ZoomImg} id='HomeImage' alt='HomeSection' /> 
      <section id='section1' className='HomeSection' data-scroll ref={section1}>
        <Navbar />
        <video src={video} id='Homevideo' alt='HomeSection' data-scroll data-scroll-speed="-3" autoPlay muted loop></video>
      </section>
    </div>
  );
}

export default HomeSection;
