import React, { useEffect, useRef } from 'react';
import LocomotiveScroll from 'locomotive-scroll';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import 'locomotive-scroll/dist/locomotive-scroll.css';
import './App.css';
import Navbar from './components/Common/Navbar';
import HomeSection from './components/BeforeLogIn/HomeSection';
import Features from './components/BeforeLogIn/Features';
import ZoomImage from '../src/Assets/Home Page Images/ZoomImage.png';

gsap.registerPlugin(ScrollTrigger);

function App() {
  const scrollRef = useRef(null);
  const imageRef = useRef(null); // Reference for the image container
  const homeSectionRef = useRef(null); // Reference for the HomeSection

  useEffect(() => {
    const scroll = new LocomotiveScroll({
      el: scrollRef.current,
      smooth: true,
      inertia: 0.85,
    });

    // GSAP Animation
    const tl = gsap.timeline({
      scrollTrigger: {
        trigger: scrollRef.current,
        start: "top top",
        end: "+=150%",
        pin: true,
        scrub: true,
        markers: true,
        invalidateOnRefresh: true,
        onUpdate: () => scroll.update(), // Update locomotive scroll
      }
    });

    tl.to(imageRef.current, {
      scale: 2,
      z: 350,
      transformOrigin: "center center",
      ease: "power1.inOut",
    })
    .to(homeSectionRef.current, {
      scale: 1.1,
      transformOrigin: "center center",
      ease: "power1.inOut",
    }, "<");

    // Recalculate locomotive scroll on window resize
    window.addEventListener('resize', () => {
      scroll.update();
    });

    return () => {
      if (scroll) scroll.destroy();
    };
  }, []);

  return (
    <body ref={scrollRef} data-scroll-container>
      <div className="wrapper">
        <div className="content">
          <div className="image-container" ref={imageRef}>
            <img src={ZoomImage} alt='Zoom' />
          </div>
          <Navbar data-scroll />
          <section ref={homeSectionRef} className="home">
            <HomeSection />
          </section>
          <Features data-scroll data-scroll-speed="4" />
          <section id='section3' data-scroll>
            <h1 data-scroll data-scroll-speed="3">Section 3</h1>
          </section>
          <section id='section4' data-scroll>
            <h1>Section 4</h1>
          </section>
          <section id='section5' data-scroll>
            <h1>Section 5</h1>
          </section>
        </div>
      </div>
    </body>
  );
}

export default App;
