import React from 'react';
import './Features.css';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import { useGSAP } from '@gsap/react';

const Features = () => {

  gsap.registerPlugin(ScrollTrigger);

  useGSAP(() => {
    const tl = gsap.timeline({
      scrollTrigger: {
        trigger: "#section2",
        start: "top top",
        end: "bottom -100%",
        pin: "#section2",
        scrub: 2,
        markers: true,
      }
    });

    tl.to(".Container", {
      height: "100%",
      width: "100%",
      borderRadius: "0px",
    })
    .from(".Heading", {
      y: -200,
      ease: "power3.inOut"
    }, "<")
    .to(".slide", {
      transform: "translateX(-301%)", 
      duration: 2, 
    }, ">")
    .to(".Content1", {
      backgroundColor: "#ff7e5f", // Change background color
      duration: 1
    }, "<") 
    .to(".Content2", {
      backgroundColor: "#feb47b", // Change background color
      duration: 1
    }, ">")
    .to(".Content3", {
      backgroundColor: "#6a11cb", // Change background color
      duration: 1
    }, ">")
    .to(".Content4", {
      backgroundColor: "#2575fc", // Change background color
      duration: 1
    }, ">")
    .to(".Heading", {
      backgroundImage: "linear-gradient(90deg, #ff7e5f, #feb47b)", // Change text gradient
      duration: 1,
    }, "<")
    .to(".Heading", {
      backgroundImage: "linear-gradient(90deg, #6a11cb, #2575fc)", // Change text gradient again
      duration: 1,
    }, ">"); 
  });

  return (
    <section id='section2'>
      <div className='Container'>
        <div className='slide'>
          <div className='Content1 Content'>
            <div className='ContentBox'></div>
          </div>
          <div className='Content2 Content'></div>
          <div className='Content3 Content'></div>
          <div className='Content4 Content'></div>
        </div>
        <div className='Heading'>YOUR ADVENTURE BEGINS HERE</div>
      </div>
    </section>
  );
}

export default Features;
