import React from 'react'
import './Features.css';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import { useGSAP } from '@gsap/react';

const Features = () => {

  gsap.registerPlugin(ScrollTrigger);


 useGSAP(()=>{

  const tl = gsap.timeline({
    scrollTrigger: {
      trigger: "#section2",
      start: "top top",
      end: "bottom top",
      pin: "#section2",
      scrub: true,
      markers: true,
    }
  });

  tl.to(".Container", {
    height: "100%",
    width: "100%",
    duration: 1 
  })
  .to(".slide", {
    transform: "translateX(-150%)", 
    duration: 5, 
  }, ">"); 
  tl.to(".Container", {
    height: "90%",
    width: "90%",
    duration: 1 
  }, ">")
 })


  return (
    <section id='section2'>
      <div className='Container'>
        <div className='slide'></div>
      </div>
    </section>
  )
}

export default Features
