import React, { useEffect, useRef } from 'react';
import LocomotiveScroll from 'locomotive-scroll';
import 'locomotive-scroll/dist/locomotive-scroll.css';
import './App.css';
import Navbar from './components/Common/Navbar';

function App() {
  const scrollRef = useRef(null);

  useEffect(() => {
    const scroll = new LocomotiveScroll({
      el: scrollRef.current,
      smooth: true,
      // inertia: 0.95,
    });

    // Recalculate locomotive scroll on window resize
    window.addEventListener('resize', () => {
      scroll.update();
    });

    return () => {
      if (scroll) scroll.destroy();
    };
  }, []);

  return (
    <body ref={scrollRef} data-scroll-container>
      <Navbar />
      <section id='section1' data-scroll-repeat>
        <h1>Section 1</h1>
      </section>
      <section id='section2' data-scroll-repeat>
        <h1>Section 2</h1>
      </section>
      <section id='section3' data-scroll-repeat>
        <h1  data-scroll data-scroll-speed="3">Section 3</h1>
      </section>
      <section id='section4' data-scroll-repeat>
        <h1>Section 4</h1>
      </section>
      <section id='section5' data-scroll-repeat>
        <h1 >Section 5</h1>
      </section>
    </body>
  );
}

export default App;
