import React, { useEffect, useRef } from 'react';
import LocomotiveScroll from 'locomotive-scroll';
import {gsap} from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import 'locomotive-scroll/dist/locomotive-scroll.css';
import './App.css';
import Navbar from './components/Common/Navbar';
import HomeSection from './components/BeforeLogIn/HomeSection';
import Features from './components/BeforeLogIn/Features';
import ZoomImage from '../src/Assets/Home Page Images/ZoomImage.png'


gsap.registerPlugin(ScrollTrigger);

function App() {
  const scrollRef = useRef(null);


  useEffect(() => {
    const scroll = new LocomotiveScroll({
      el: scrollRef.current,
      smooth: true,
      inertia: 0.85,
    });

    ScrollTrigger.scrollerProxy(scrollRef.current, {
      scrollTop(value) {
        return arguments.length
          ? scroll.scrollTo(value, 0, 0)
          : scroll.scroll.instance.scroll.y;
      },
      getBoundingClientRect() {
        return { top: 0, left: 0, width: window.innerWidth, height: window.innerHeight };
      },
      pinType: scrollRef.current.style.transform ? 'transform' : 'fixed',
    });

    gsap.to("#ZoomImage", {
      transform: "scale(2)",
      ScrollTrigger:{
        scroller: "body",
        trigger: "#ZoomImage",
        start: "10% 5%",
        end: "bottom top",
        pin:"#home",
        scrub: 0.25,
        markers:true,
        onUpdate: ()=> scroll.update(),
      },
    });




    // Recalculate locomotive scroll on window resize
    ScrollTrigger.addEventListener('refresh', () => scroll.update());

    ScrollTrigger.refresh();

    // Recalculate Locomotive Scroll on window resize
    window.addEventListener('resize', () => {
      scroll.update();
    });

    return () => {
      scroll.destroy();
      ScrollTrigger.removeEventListener('refresh', () => scroll.update());
    };
  }, []);





  return (
    <>

    <body ref={scrollRef} id='body' data-scroll-container>
    <img src={ZoomImage} alt='Zoom' id='ZoomImage'/>
    <div id='content'> 
      <Navbar data-scroll/>
      <HomeSection className="home" id="home"/>
      <Features data-scroll data-scroll-speed="4"/>
      <section id='section3' data-scroll>
        <h1  data-scroll data-scroll-speed="3">Section 3</h1>
      </section>
      <section id='section4' data-scroll>
        <h1>Section 4</h1>
      </section>
      <section id='section5' data-scroll>
        <h1 >Section 5</h1>
      </section>
      </div>
    </body>
    </>
  );
}

export default App;
