import React, {useEffect} from 'react';
import {gsap} from 'gsap';
import LocomotiveScroll from 'locomotive-scroll';
import 'locomotive-scroll/dist/locomotive-scroll.css';
// import { ScrollTrigger } from 'gsap/ScrollTrigger';
import './HomeSection.css';
import HomeImage from '../../Assets/Home Page Images/pexels-photo-547115.png';
import ZoomImage from '../../Assets/Home Page Images/ZoomImage.png';


const HomeSection = () => {
  const scrollRef = useRef(null);
  

  useEffect(() => {
    const scroll = new LocomotiveScroll({
      el: scrollRef.current,
      smooth: true,
      inertia: 0.85,
    });

    
    // gsap
    // .to(".top-image",{
    //   transform:"scale(2)",
    //   scrollTrigger: {
    //     trigger: ".top-image",
    //     start: "top top",
    //     end: "bottom top",
    //     pin: ".HomeSection",
    //     scrub: true,
    //     markers: true,
    //     onLeave: () => scroll.update(),
    //   }
    // })

    gsap.to (".top-image", {
      transform: "scale(2)",
      scrollTrigger: {
        trigger: ".HomeSection",
        scroller: "body",
        start: "top top",
        end: "bottom top",
        scrub: true,
        pin: ".HomeSection",
        markers:true,
        onLeave: ()=> scroll.update,
      },
    })
    

    // Recalculate locomotive scroll on window resize
    window.addEventListener('resize', () => {
      scroll.update();
    });

    return () => {
      if (scroll) scroll.destroy();
    };
  }, []);

  
  

  return (
    <div className='HomeSection' >
      <img src={ZoomImage}/>
    <section id='section1' className='HomeSection' data-scroll>
        <img src={HomeImage} id='HomeImage' alt='HomeSection' data-scroll data-scroll-speed="-3"/>
    </section>
    </div>
  )
}

export default HomeSection;
