import React, {useEffect, useRef} from 'react';
import './App.css';
import Navbar from './components/Common/Navbar';
import LocomotiveScroll from 'locomotive-scroll';
import 'locomotive-scroll/dist/locomotive-scroll.css';

function App() {

  const scrollRef = useRef(null);

  useEffect(() => {
    const scroll = new LocomotiveScroll({
      el: scrollRef.current,
      smooth: true,
      getDirection: true,
      inertia: 0.75,
    });

    return () => {
      if (scroll) scroll.destroy();
    };
  }, []);


  return (
    <body id='scroll-container' data-scroll-container ref={scrollRef}>
      <Navbar/>
      <section id='section1'  data-scroll-section>
      <h1 data-scroll data-scroll-speed="1">Hello, World!</h1>
      <p data-scroll data-scroll-speed="2">This is a scrolling section.</p>
      </section>
      <section id='section2' data-scroll-section >
      <h1 data-scroll data-scroll-speed="1">Hello, World!</h1>
      <p data-scroll data-scroll-speed="2">This is a scrolling section.</p>
      </section>
      <section id='section3' data-scroll data-scroll-speed="2">

      </section>





    </body>

  );
}

export default App;
