import React, { useRef, useEffect } from 'react';
import './HomeSection.css';
import Navbar from '../Common/Navbar';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import ZoomImage from '../../Assets/Home Page Images/ZoomImage.png';
import video from '../../Assets/Home Page Images/Video.mp4';

const HomeSection = () => {
  const ZoomImg = useRef(null);
  const videoRef = useRef(null);  // Create a ref for the video
  gsap.registerPlugin(ScrollTrigger);

  useEffect(() => {
    const tl = gsap.timeline({
      scrollTrigger: {
        trigger: ZoomImg.current,
        scroller: "body",
        start: "top top",
        end: "center top",
        pin: "#HomeSection",
        scrub: true,
        markers: true,
      }
    });

    // Animate the image
    tl.to(ZoomImg.current, {
      scale: 8,
    })
    // Add the video animation after the image animation completes
    .to(videoRef.current, {
      height: "95%",
      width: "95%",
      scrub: true,
    }, ">"); // The "<" ensures this animation starts at the same time as the image animation ends

  }, []);

  return (
    <div className='HomeSection' id='HomeSection'>
      <img src={ZoomImage} ref={ZoomImg} id='HomeImage' alt='HomeSection' />
      <section id='section1' className='HomeSection' data-scroll>
        <Navbar />
        <video
          src={video}
          ref={videoRef}
          id='Homevideo'
          alt='HomeSection'
          data-scroll
          data-scroll-speed="-3"
          autoPlay
          muted
          loop
        ></video>
      </section>
    </div>
  );
};

export default HomeSection;
