import React, { useRef, useEffect } from 'react';
import './HomeSection.css';
import Navbar from '../Common/Navbar';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import ZoomImage from '../../Assets/Home Page Images/ZoomImage.png';
import video from '../../Assets/Home Page Images/Video.mp4';

gsap.registerPlugin(ScrollTrigger);

const HomeSection = () => {
  const ZoomImg = useRef(null);
  const videoRef = useRef(null);

  useEffect(() => {
    const imageAnimation = gsap.fromTo(ZoomImg.current, 
      { scale: 1 }, 
      { 
        scale: 8, 
        scrollTrigger: {
          trigger: ZoomImg.current,
          scroller: "body",
          start: "top top",
          end: "center top",
          pin: "#HomeSection",
          scrub: true,
          markers: true,
          onUpdate: (self) => {
            // Update the video scale based on the image animation progress
            const progress = self.progress;
            if (videoRef.current) {
              gsap.to(videoRef.current, {
                scale: progress * 0.9 + 0.1, // Scale the video between 0.1 and 0.9
                transformOrigin: "center center", // Ensure scaling from center
                duration: 0.1,
                overwrite: "auto"
              });
            }
          }
        }
      }
    );
    
    return () => {
      imageAnimation.kill();
    };
  }, []);

  return (
    <div className='HomeSection' id='HomeSection'>
      <img src={ZoomImage} ref={ZoomImg} id='HomeImage' alt='HomeSection' /> 
      <section id='section1' className='HomeSection' data-scroll>
        <Navbar />
        <video src={video} ref={videoRef} id='Homevideo' alt='HomeSection' data-scroll data-scroll-speed="-3" autoPlay muted loop></video>
      </section>
    </div>
  );
};

export default HomeSection;
